import React, {useEffect, useState} from "react";



function App() {
  let arr;

  fetch('http://localhost:3001/api/get').then(response =>
  {
    if (response.ok)
    {
      return response.json();
    }
    else
    {
      throw new Error('Ошибка при получении данных: ' + response.status);
    }
  }).then(data =>
  {
    console.log(data);
    arr[0] = data.message1;
    arr[1] = data.message2;
  }).catch(error => console.error(error));

  const [value, setValue] = useState(0);

  useEffect(() => {
    var xhr = new XMLHttpRequest();
    xhr.open("POST", "http://localhost:3001/api/endpoint", true);
    xhr.setRequestHeader("Content-Type", "application/json;charset=UTF-8");
    xhr.onreadystatechange = function() {
      if (xhr.readyState === XMLHttpRequest.DONE) {
        if (xhr.status === 200) {
          // Обработка успешного ответа от сервера
          console.log("Успешный ответ сервера:", xhr.responseText);
        } else {
          // Обработка ошибки от сервера
          console.error("Ошибка сервера:", xhr.status, xhr.statusText);
        }
      }
    };
    var data = {message3: `hello frontend and number ${value}`}; // данные для отправки на сервер
    xhr.send(JSON.stringify(data));
  }, [value])

  return (
    <div className="App">
      <h1>{arr[0]}</h1>
      <h2>{arr[1]}</h2>
      <button onClick={() => setValue(value + 1)}>{value}</button>
    </div>
  );
}

export default App;
